package org.onlineshop.serviceimpl;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import org.springframework.transaction.annotation.Transactional;import org.onlineshop.dao.ShippingAddressDao;import org.onlineshop.model.ShippingAddress;import org.onlineshop.service.ShippingAddressService;@Servicepublic class ShippingAddressServiceImpl implements ShippingAddressService{    @Autowired    private ShippingAddressDao shippingAddressDao;    @Transactional()    @Override    public void addShippingAddress(ShippingAddress shippingAddress) {        shippingAddressDao.save(shippingAddress);    }    @Transactional(readOnly = true)    @Override    public ShippingAddress getShippingAddressById(long shippingAddressId) {        return shippingAddressDao.findOne(shippingAddressId);    }}