package org.onlineshop.validator;import org.springframework.stereotype.Component;import org.springframework.validation.Errors;import org.springframework.validation.Validator;import org.onlineshop.model.Customer;@Componentpublic class PasswordValidator implements Validator {    @Override    public boolean supports(Class<?> paramClass) {        return Customer.class.equals(paramClass);    }    @Override    public void validate(Object target, Errors errors) {        Customer customer=(Customer) target;        if(customer.getPassword().length()<6 || customer.getPassword().length()>30){            errors.rejectValue("password", null, "Password must be between 6 and 30 character");        }    }}